import React, { useState, useEffect } from 'react';

const Timetable = () => {
  // State variables
  const [timetableData, setTimetableData] = useState([]);
  const [studentRollNo, setStudentRollNo] = useState('');
  const [department, setDepartment] = useState('');
  const [semester, setSemester] = useState('');

  // Fetch department and semester based on student roll number
  useEffect(() => {
    const fetchStudentInfo = async () => {
      try {
        // Fetch student information based on roll number
        // const studentResponse = await fetch(`/api/studentInfo?rollNo=${studentRollNo}`);
        // const studentData = await studentResponse.json();

        // Update department and semester state based on student information
        // setDepartment(studentData.department);
        // setSemester(studentData.semester);
      } catch (error) {
        console.error('Error fetching student information:', error);
      }
    };

    fetchStudentInfo();
  }, [studentRollNo]);

  // Fetch timetable data based on department and semester
  useEffect(() => {
    const fetchTimetable = async () => {
      try {
        // Fetch timetable data based on department and semester
        // const response = await fetch(
        //   `/api/timetable?department=${department}&semester=${semester}`
        // );
        // const data = await response.json();
        // setTimetableData(data);
      } catch (error) {
        console.error('Error fetching timetable:', error);
      }
    };

    // Fetch timetable data only if both department and semester are available
    if (department && semester) {
      fetchTimetable();
    }
  }, [department, semester]);

  return (
    <div>
      <h2>Timetable for {department} - Semester {semester}</h2>
      {timetableData.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>TimetableID</th>
              <th>TeacherName</th>
              <th>SubjectName</th>
              <th>DayOfWeek</th>
              <th>PeriodNumber</th>
            </tr>
          </thead>
          <tbody>
            {timetableData.map((item) => (
              <tr key={item.TimetableID}>
                <td>{item.TimetableID}</td>
                <td>{item.TeacherName}</td>
                <td>{item.SubjectName}</td>
                <td>{item.DayOfWeek}</td>
                <td>{item.PeriodNumber}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>No timetable data available.</p>
      )}
    </div>
  );
};

export default Timetable;
