import React, { useState, useEffect } from 'react';

const StudentData = ({ rollNo }) => {
  const [studentData, setStudentData] = useState(null);

  useEffect(() => {
    // Assuming you have an API endpoint to fetch student data by RollNo
    const fetchStudentData = async () => {
      try {
        const response = await fetch(`/api/student/${rollNo}`);
        const data = await response.json();
        setStudentData(data);
      } catch (error) {
        console.error('Error fetching student data:', error);
      }
    };

    fetchStudentData();
  }, [rollNo]);

  return (
    <div>
      {studentData ? (
        <table>
          <thead>
            <tr>
              <th>AssignmentId</th>
              <th>RollNo</th>
              <th>TeacherId</th>
              <th>SubjectId</th>
              <th>DateofEsu</th>
              <th>DateofSubmit</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {studentData.map((assignment) => (
              <tr key={assignment.AssignmentId}>
                <td>{assignment.AssignmentId}</td>
                <td>{assignment.RollNo}</td>
                <td>{assignment.TeacherId}</td>
                <td>{assignment.SubjectId}</td>
                <td>{assignment.DateofEsu}</td>
                <td>{assignment.DateofSubmit}</td>
                <td>{assignment.Status}</td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Loading student data...</p>
      )}
    </div>
  );
};

export default StudentData;
